extension.install:
// ?><?php
// Add field for parent forum
if (!$forum_db->field_exists('forums', 'parent_id'))
{
	$forum_db->add_field('forums', 'parent_id', 'INT(10) UNSIGNED', false, '0');
	if ($forum_db->field_exists('forums', 'pun_subforums_parent_id')) // FOR DELETE !!!
	{
		$subforums_query = array(
			'UPDATE'	=> 'forums',
			'SET'		=> 'parent_id=pun_subforums_parent_id'
		);
		$forum_db->query_build($subforums_query) or error(__FILE__, __LINE__);
	}
}

// Add field for show/hide subforums on index.php for each user
if (!$forum_db->field_exists('users', 'show_subforums_list'))
{
	$forum_db->add_field('users', 'show_subforums_list', 'TINYINT UNSIGNED', true, '1');
	if ($forum_db->field_exists('users', 'show_subforums_desc'))
	{
		$query = array(
			'UPDATE'	=> 'users',
			'SET'		=> 'show_subforums_list=show_subforums_desc+1',
		);
		$forum_db->query_build($query) or error(__FILE__, __LINE__);
	}
}
// Drop old field
if ($forum_db->field_exists('users', 'show_subforums_desc'))
	$forum_db->drop_field('users', 'show_subforums_desc');

// Add extension options to the config table
if (!isset($forum_config['o_subforums_plain_view'])) // Plain or full view of subforums on viewforum.php
{
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_subforums_plain_view\', \'0\'' // 0 - full
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}
if (!isset($forum_config['o_subforums_show_list'])) // Show subforums (& their descriptions) on index.php
{
	$subforums_show_list = 1; // 1 - list w/o descriptions
	if (isset($forum_config['o_subforums_show_desc']))
	{
		if ($forum_config['o_subforums_show_desc'] == 1)
			$subforums_show_list = 2; // 2 - list with descriptions

		// Delete old option from the config
		$query = array(
			'DELETE'	=> 'config',
			'WHERE'		=> 'conf_name=\'o_subforums_show_desc\'',
		);
		$forum_db->query_build($query) or error(__FILE__, __LINE__);
	}
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_subforums_show_list\', \''.$subforums_show_list.'\''
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}
if (!isset($forum_config['o_subforums_create_link'])) // Show create link on viewforum.php
{
	$query = array(
		'INSERT'	=> 'conf_name, conf_value',
		'INTO'		=> 'config',
		'VALUES'	=> '\'o_subforums_create_link\', \'0\'' // 0 - not show
	);
	$forum_db->query_build($query) or error(__FILE__, __LINE__);
}

extension.uninstall:


// Remove field for parent forum
if ($forum_db->field_exists('forums', 'parent_id'))
	$forum_db->drop_field('forums', 'parent_id');

// Remove field for show/hide subforums
if ($forum_db->field_exists('users', 'show_subforums_list'))
	$forum_db->drop_field('users', 'show_subforums_list');
if ($forum_db->field_exists('users', 'show_subforums_desc'))
	$forum_db->drop_field('users', 'show_subforums_desc');

// Delete extension options from the config
$query = array(
	'DELETE'	=> 'config',
	'WHERE'		=> 'conf_name IN ( \'o_subforums_show_list\', \'o_subforums_show_desc\', \'o_subforums_create_link\')',
);
$forum_db->query_build($query) or error(__FILE__, __LINE__);

