	// -> Exist forums
	$subforums_query = array(
		'SELECT'	=> 'c.id AS cid, c.cat_name, f.id AS fid, f.forum_name',
		'FROM'		=> 'categories AS c',
		'JOINS'		=> array(
			array(
				'INNER JOIN'	=> 'forums AS f',
				'ON'			=> 'c.id=f.cat_id'
			)
		),
		'WHERE'		=> 'f.parent_id=0',
		'ORDER BY'	=> 'c.disp_position, c.id, f.disp_position'
	);
	$subforums_result = $forum_db->query_build($subforums_query) or error(__FILE__, __LINE__);

	$parent_category = 0;
	$forum_count = 0;		// for subforums_option()
	$subforums_tab = 7;		// for subforums_option()
	while ($cur_parent_forum = $forum_db->fetch_assoc($subforums_result))
	{
		if ($cur_parent_forum['cid'] != $parent_category) // New category since last iteration
		{
			if ($parent_category)
				echo "\t\t\t\t\t\t\t".'</optgroup>'."\n";
			echo "\t\t\t\t\t\t\t".'<optgroup label="'.forum_htmlencode($cur_parent_forum['cat_name']).'">'."\n";
			$parent_category = $cur_parent_forum['cid'];
		}
		echo "\n\t\t\t\t\t\t\t".'<option value="'.$cur_parent_forum['fid'].'">'.forum_htmlencode($cur_parent_forum['forum_name']).'</option>';
		echo subforums_option($cur_parent_forum['fid'], 1);
	}

	// -> End of field
