// Load functions file
require_once $ext_info['path'].'/functions.php';

// Get subforums table
if (!isset($subforums))
	$subforums = subforums_get();

$subforums_tree = subforums_tree($forum_id);

$query['WHERE'] = 't.forum_id IN ('.implode(', ', $subforums_tree).')';

// Go to parent forum
$subforums_query = array(
	'SELECT'	=> 'f.parent_id',
	'FROM'		=> 'forums as f',
	'WHERE'		=> 'id='.$forum_id
);
$subforums_result = $forum_db->query_build($subforums_query) or error(__FILE__, __LINE__);

if ($cur_subforum = $forum_db->fetch_assoc($subforums_result))
{
	if ($cur_subforum['parent_id'] != 0)
		sync_forum($cur_subforum['parent_id']);
}
